import React, { useEffect, useState } from "react";
import { ethers } from "ethers";
import VideoVerification from "./contracts/VideoVerification.json";
import { CONTRACT_ADDRESS } from "./constants";

function App() {
  const [wallet, setWallet] = useState("");
  const [contract, setContract] = useState(null);

  const [videoId, setVideoId] = useState("");
  const [phash, setPhash] = useState("");
  const [dctHash, setDctHash] = useState("");
  const [histHash, setHistHash] = useState("");
  const [aiHash, setAiHash] = useState("");

  const [status, setStatus] = useState("");
  const [score, setScore] = useState(null);
  const [fetchedVideo, setFetchedVideo] = useState(null);

  useEffect(() => {
    connectWallet();
  }, []);

  async function connectWallet() {
    try {
      if (!window.ethereum) {
        alert("MetaMask not found. Please install it.");
        return;
      }

      // Request account access
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      await provider.send("eth_requestAccounts", []);
      const signer = provider.getSigner();

      const address = await signer.getAddress();
      setWallet(address);

      const contractInstance = new ethers.Contract(
        CONTRACT_ADDRESS,
        VideoVerification.abi,
        signer
      );
      setContract(contractInstance);
    } catch (err) {
      console.error("Wallet connection failed:", err);
      setStatus(`‚ùå ${err.message}`);
    }
  }

  const uploadGenuine = async () => {
    try {
      const tx = await contract.uploadGenuineVideo(
        videoId,
        phash,
        dctHash,
        histHash,
        aiHash
      );
      await tx.wait();
      setStatus("‚úÖ Genuine video uploaded.");
    } catch (e) {
      setStatus(`‚ùå Error: ${e.message}`);
    }
  };

  const logDeepfake = async () => {
    try {
      const tx = await contract.logDeepfakeAttempt(
        videoId,
        phash,
        dctHash,
        histHash,
        aiHash
      );
      await tx.wait();
      setStatus("‚ö†Ô∏è Deepfake attempt logged.");
    } catch (e) {
      setStatus(`‚ùå Error: ${e.message}`);
    }
  };

  const requestPermission = async () => {
    try {
      const tx = await contract.requestPermission(videoId);
      await tx.wait();
      setStatus("‚úçÔ∏è Permission requested.");
    } catch (e) {
      setStatus(`‚ùå Error: ${e.message}`);
    }
  };

  const grantPermission = async (newUploader, newVideoId) => {
    try {
      const tx = await contract.grantPermission(videoId, newUploader, newVideoId);
      await tx.wait();
      setStatus("‚úÖ Permission granted.");
    } catch (e) {
      setStatus(`‚ùå Error: ${e.message}`);
    }
  };

  const getVideoDetails = async () => {
    try {
      const data = await contract.getVideoDetails(videoId);
      setFetchedVideo(data);
    } catch (e) {
      setStatus(`‚ùå Error: ${e.message}`);
    }
  };

  const getScore = async () => {
    try {
      const s = await contract.getUserGenuineScore(wallet);
      setScore(s.toString());
    } catch (e) {
      setStatus(`‚ùå Error: ${e.message}`);
    }
  };

  return (
    <div style={{ padding: "2rem", fontFamily: "monospace" }}>
      <h2>üìº Video Verification DApp</h2>

      {wallet ? (
        <p>üü¢ Connected Wallet: {wallet}</p>
      ) : (
        <button onClick={connectWallet}>üîå Connect Wallet</button>
      )}

      <br />

      <input
        placeholder="Video ID (IPFS)"
        value={videoId}
        onChange={(e) => setVideoId(e.target.value)}
      /><br />
      <input
        placeholder="pHash"
        value={phash}
        onChange={(e) => setPhash(e.target.value)}
      /><br />
      <input
        placeholder="DCT Hash"
        value={dctHash}
        onChange={(e) => setDctHash(e.target.value)}
      /><br />
      <input
        placeholder="Histogram Hash"
        value={histHash}
        onChange={(e) => setHistHash(e.target.value)}
      /><br />
      <input
        placeholder="AI Features Hash"
        value={aiHash}
        onChange={(e) => setAiHash(e.target.value)}
      /><br />

      <button onClick={uploadGenuine}>‚úÖ Upload Genuine</button>
      <button onClick={logDeepfake}>‚ö†Ô∏è Log Deepfake</button>
      <button onClick={requestPermission}>‚úçÔ∏è Request Permission</button>
      <br /><br />

      <button
        onClick={() =>
          grantPermission(prompt("New uploader address:"), prompt("New Video ID:"))
        }
      >
        ‚úÖ Grant Permission
      </button>
      <button onClick={getVideoDetails}>üìÑ Fetch Video Details</button>
      <button onClick={getScore}>‚≠ê Get My Genuine Score</button>

      <p style={{ color: "green", marginTop: "1rem" }}>{status}</p>

      {fetchedVideo && (
        <div style={{ marginTop: "1rem" }}>
          <h4>üìÑ Video Info:</h4>
          <p>Uploader: {fetchedVideo[0]}</p>
          <p>Is Deepfake: {fetchedVideo[1] ? "Yes" : "No"}</p>
          <p>Timestamp: {new Date(fetchedVideo[2] * 1000).toLocaleString()}</p>
          <p>Original Owner: {fetchedVideo[3]}</p>
          <p>Permissioned Reuse: {fetchedVideo[4] ? "Yes" : "No"}</p>
        </div>
      )}

      {score && (
        <p style={{ marginTop: "1rem" }}>‚≠ê Genuine Score: {score}</p>
      )}
    </div>
  );
}

export default App;
