import React, { useEffect, useState } from "react";
import { BrowserProvider, Contract } from "ethers";
import VideoVerification from "./contracts/VideoVerification.json";
import { CONTRACT_ADDRESS } from "./constants";

function App() {
  const [wallet, setWallet] = useState(null);
  const [contract, setContract] = useState(null);
  const [videoId, setVideoId] = useState("");
  const [phash, setPhash] = useState("");
  const [dctHash, setDctHash] = useState("");
  const [histHash, setHistHash] = useState("");
  const [aiHash, setAiHash] = useState("");
  const [isGenuine, setIsGenuine] = useState(true);

  const [status, setStatus] = useState("");
  const [score, setScore] = useState(null);
  const [fetchedVideo, setFetchedVideo] = useState(null);

  useEffect(() => {
    connectWallet();
  }, []);

  async function connectWallet() {
    if (window.ethereum) {
      const provider = new BrowserProvider(window.ethereum);
      const signer = await provider.getSigner();
      const address = await signer.getAddress();
      setWallet(address);

      const vc = new Contract(CONTRACT_ADDRESS, VideoVerification.abi, signer);
      setContract(vc);
    } else {
      alert("Install MetaMask!");
    }
  }

  async function upload() {
    try {
      const tx = isGenuine
        ? await contract.uploadGenuineVideo(videoId, phash, dctHash, histHash, aiHash)
        : await contract.logDeepfakeAttempt(videoId, phash, dctHash, histHash, aiHash);
      await tx.wait();
      setStatus(isGenuine ? "Genuine video uploaded ✅" : "Deepfake attempt logged ⚠️");
    } catch (e) {
      setStatus(`❌ Error: ${e.shortMessage || e.message}`);
    }
  }

  async function requestPermission() {
    try {
      const tx = await contract.requestPermission(videoId);
      await tx.wait();
      setStatus("Permission requested ✍️");
    } catch (e) {
      setStatus(`❌ Error: ${e.shortMessage || e.message}`);
    }
  }

  async function grantPermission(newUploader, newVideoId) {
    try {
      const tx = await contract.grantPermission(videoId, newUploader, newVideoId);
      await tx.wait();
      setStatus("Permission granted ✅");
    } catch (e) {
      setStatus(`❌ Error: ${e.shortMessage || e.message}`);
    }
  }

  async function getVideoDetails() {
    try {
      const result = await contract.getVideoDetails(videoId);
      setFetchedVideo(result);
    } catch (e) {
      setStatus(`❌ Error: ${e.shortMessage || e.message}`);
    }
  }

  async function getScore() {
    try {
      const result = await contract.getUserGenuineScore(wallet);
      setScore(result.toString());
    } catch (e) {
      setStatus(`❌ Error: ${e.shortMessage || e.message}`);
    }
  }

  return (
    <div style={{ padding: "2rem", fontFamily: "monospace" }}>
      <h2>📼 Video Verification DApp</h2>
      <p>Connected Wallet: {wallet}</p>

      <input placeholder="Video ID (IPFS)" value={videoId} onChange={(e) => setVideoId(e.target.value)} /><br />
      <input placeholder="pHash" value={phash} onChange={(e) => setPhash(e.target.value)} /><br />
      <input placeholder="DCT Hash" value={dctHash} onChange={(e) => setDctHash(e.target.value)} /><br />
      <input placeholder="Histogram Hash" value={histHash} onChange={(e) => setHistHash(e.target.value)} /><br />
      <input placeholder="AI Features Hash" value={aiHash} onChange={(e) => setAiHash(e.target.value)} /><br />

      <label>
        Is this a Genuine Video?&nbsp;
        <select value={isGenuine.toString()} onChange={(e) => setIsGenuine(e.target.value === "true")}>
          <option value="true">True</option>
          <option value="false">False</option>
        </select>
      </label>
      <br />
      <button onClick={upload}>Upload</button>
      <button onClick={requestPermission}>Request Permission</button>
      <br /><br />

      <button onClick={() => grantPermission(prompt("New uploader address:"), prompt("New Video ID:"))}>Grant Permission</button>
      <button onClick={getVideoDetails}>Fetch Video Details</button>
      <button onClick={getScore}>Get My Genuine Score</button>

      <p style={{ color: "green" }}>{status}</p>

      {fetchedVideo && (
        <div>
          <h4>📄 Video Info:</h4>
          <p>Uploader: {fetchedVideo[0]}</p>
          <p>Is Deepfake: {fetchedVideo[1] ? "Yes" : "No"}</p>
          <p>Timestamp: {new Date(Number(fetchedVideo[2]) * 1000).toLocaleString()}</p>
          <p>Original Owner: {fetchedVideo[3]}</p>
          <p>Permissioned Reuse: {fetchedVideo[4] ? "Yes" : "No"}</p>
        </div>
      )}

      {score && <p>⭐ Genuine Score: {score}</p>}
    </div>
  );
}

export default App;
